/**
 * Standard API success response structure
 */
export interface ApiSuccessResponse<T = unknown> {
  success: true;
  data: T;
  message?: string;
  pagination?: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
  metadata?: Record<string, unknown>;
}

/**
 * Standard API error response structure
 */
export interface ApiErrorResponse {
  success: false;
  error: {
    message: string;
    code?: string;
    details?: Record<string, unknown>;
    field?: string; // For validation errors
  };
  timestamp: string;
  path?: string;
}

/**
 * Union type for all API responses
 */
export type ApiResponse<T = any> = ApiSuccessResponse<T> | ApiErrorResponse;

/**
 * Alternative API response format (from authentication/types.ts)
 * Used for standardized API responses following API Design Standards
 */
export interface StandardApiResponse<T> {
  data: T;
  message?: string;
  metadata?: {
    timestamp: string;
    version: string;
    requestId?: string;
  };
}

/**
 * Alternative paginated response format (from authentication/types.ts)
 * Used for standardized paginated API responses
 */
export interface StandardPaginatedResponse<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    pages: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
  message?: string;
  metadata?: {
    timestamp: string;
    version: string;
    requestId?: string;
  };
}

/**
 * Alternative error response format (from authentication/types.ts)
 * Used for detailed error responses with request tracking
 */
export interface StandardErrorResponse {
  error: {
    code: string;
    message: string;
    details?: Record<string, unknown> | string[] | string;
    field?: string;
  };
  timestamp: string;
  path: string;
  requestId?: string;
}

