  /** Error code for programmatic handling */
  code: string;
  /** Human-readable error message */
  message: string;
  /** Additional error details */
  details?: Record<string, unknown> | string;
  /** Field name if error is field-specific */
  field?: string;
  /** Error timestamp */
  timestamp?: string;
}

