  });

  return {
    transformedData: result.transformedData,
    auditInfo: {
      ...result.auditInfo!,
      hasVoterData: !!(formData.is_voter || formData.is_resident_voter),
    },
  };
}

/**
 * Get field validation error message
 */
export function getFieldErrorMessage(field: string, value: any): string | null {
  if (!value) return null;

  switch (field) {
    case 'email':
      return VALIDATION_RULES.EMAIL_PATTERN.test(value) ? null : 'Invalid email format';

    case 'mobile_number':
    case 'telephone_number':
      return VALIDATION_RULES.PHONE_PATTERN.test(value) ? null : 'Invalid phone number format';

    case 'philsys_card_number':
      return VALIDATION_RULES.PHILSYS_PATTERN.test(value)
        ? null
        : 'Invalid PhilSys card format (XXXX-XXXX-XXXX)';

    case 'first_name':
    case 'middle_name':
    case 'last_name':
    case 'extension_name':
      return VALIDATION_RULES.NAME_PATTERN.test(value) ? null : 'Name contains invalid characters';

    default:
      return null;
  }
}

/**
 * Check if form data has changed significantly (for preventing duplicate submissions)
 */
export function hasSignificantChanges(oldData: any, newData: any): boolean {
  const significantFields = [
    'first_name',
    'last_name',
    'birthdate',
    'philsys_card_number',
    'household_code',
  ];

  return significantFields.some(field => oldData[field] !== newData[field]);
}

/**
 * Generate form summary for audit purposes (non-PII)
 */
export function generateFormSummary(formData: any): Record<string, any> {
  return {
    hasPersonalInfo: !!(formData.first_name && formData.last_name),
    hasContactInfo: !!(formData.email || formData.mobile_number),
    hasPhilSysCard: !!formData.philsys_card_number,
    hasVoterInfo: !!(formData.is_voter || formData.is_resident_voter),
    hasHouseholdAssignment: !!formData.household_code,
    educationLevel: formData.education_attainment || 'not_specified',
    employmentStatus: formData.employment_status || 'not_specified',
    civilStatus: formData.civil_status || 'not_specified',
    citizenship: formData.citizenship || 'not_specified',
    fieldCount: Object.keys(formData).length,
  };
}

/**
 * Sanitization Utilities
 * CONSOLIDATED - Single source of truth for input sanitization
 * Philippine government-grade input sanitization following BSP Circular 808 and NIST standards
 * Database schema aligned - PhilSys format: 12 digits (VARCHAR(20))
 */

import DOMPurify from 'isomorphic-dompurify';

import type { SanitizationType, SanitizationOptions } from '@/types/utilities';

/**
 * Default field type mapping for intelligent sanitization
 * Aligned with database schema constraints
 */
const DEFAULT_FIELD_TYPE_MAPPING: Readonly<Record<string, SanitizationType>> = {
  first_name: 'name',
  middle_name: 'name',
  last_name: 'name',
  extension_name: 'name',
  mother_maiden_first: 'name',
  mother_maiden_middle: 'name',
  mother_maiden_last: 'name',
  email: 'email',
  mobile_number: 'mobile',
  telephone_number: 'text',
  philsys_card_number: 'philsys',
  region_code: 'psgc',
  province_code: 'psgc',
  city_municipality_code: 'psgc',
  barangay_code: 'psgc',
  height: 'numeric',
  weight: 'numeric',
} as const;

/**
 * Sanitize general input to prevent XSS and injection attacks
 * Consolidated from multiple implementations across lib and utils
 */
export function sanitizeInput(input: string | null, options: SanitizationOptions = {}): string {
  if (!input) return '';

  // Early length check to avoid unnecessary processing
  if (options.maxLength && input.length > options.maxLength * 2) {
    input = input.substring(0, options.maxLength * 2);
  }

  let result: string;

  // Remove potentially dangerous characters and scripts
  result = input
    .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
    .replace(/[<>]/g, '')
    .replace(/javascript:/gi, '')
    .replace(/on\w+\s*=/gi, '')
    .replace(/data:/gi, '')
    .replace(/vbscript:/gi, '')
    .replace(/expression\s*\(/gi, '');

  // Apply options
  if (options.normalizeUnicode !== false) {
    result = result.normalize('NFC');
  }

  if (options.trim !== false) {
    result = result.trim();
  }

  if (options.removeHtml !== false) {
    result = result.replace(/<[^>]*>/g, '');
  }

  if (options.removeScripts !== false) {
    result = result
      .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
      .replace(/javascript:/gi, '')
      .replace(/on\w+\s*=/gi, '');
  }

  if (options.allowedChars && options.allowedChars instanceof RegExp) {
    result = result.replace(new RegExp(`[^${options.allowedChars.source}]`, 'g'), '');
  }

  if (options.maxLength && result.length > options.maxLength) {
    result = result.substring(0, options.maxLength);
  }

  // Use DOMPurify for additional sanitization
  return DOMPurify.sanitize(result, {
    ALLOWED_TAGS: [],
    ALLOWED_ATTR: [],
  }).trim();
}

/**
 * Sanitize HTML content while allowing safe tags
 */
export function sanitizeHtml(html: string): string {
  if (!html || typeof html !== 'string') {
    return '';
  }

  // Remove dangerous patterns that could bypass DOMPurify
  const dangerousPatterns = [
    /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
    /javascript:/gi,
    /vbscript:/gi,
    /on\w+\s*=/gi,
    /data:/gi,
    /expression\s*\(/gi,
    /<iframe\b[^>]*>/gi,
    /<object\b[^>]*>/gi,
    /<embed\b[^>]*>/gi,
    /<link\b[^>]*>/gi,
    /<meta\b[^>]*>/gi,
  ];

  let sanitized = html;
  dangerousPatterns.forEach(pattern => {
    sanitized = sanitized.replace(pattern, '');
  });

  return sanitized.trim();
}

/**
 * Sanitize name input for Philippine names
 * Supports Filipino naming patterns, Spanish influences, and indigenous names
 * Database constraint: VARCHAR(100)
 */
export function sanitizeName(name: string): string {
  if (!name || typeof name !== 'string') {
    return '';
  }

  return sanitizeInput(name, {
    allowedChars: /[a-zA-Z√Ä-√ø√±√ë\s\-'.]/,
    maxLength: 100,
    normalizeUnicode: true,
    trim: true,
  });
}

/**
 * Validate name input format
 */
export function validateNameInput(name: string): boolean {
  // Philippine name validation pattern
  // Supports: Juan, Mar√≠a Jos√©, Rizal y L√≥pez, O'Connor, Dela Cruz Jr.
  const nameRegex = /^[a-zA-Z√Ä-√ø√±√ë\s\-'.]{1,100}$/;
  return nameRegex.test(name) && name.trim().length > 0;
}

/**
 * Sanitize PhilSys card number format
 * CORRECT FORMAT: 12 digits formatted as XXXX-XXXX-XXXX
 * Database constraint: VARCHAR(20)
 */
export function sanitizePhilSysNumber(input: string | null): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  // Remove all non-digits and format properly
  const digitsOnly = input.replace(/\D/g, '');

  // Validate length (PhilSys is 12 digits, not 16!)
  if (digitsOnly.length !== 12) {
    return '';
  }

  // Format as XXXX-XXXX-XXXX (14 characters with dashes)
  return `${digitsOnly.substring(0, 4)}-${digitsOnly.substring(4, 8)}-${digitsOnly.substring(8, 12)}`;
}

/**
 * Validate PhilSys card number format
 * CORRECT: 12 digits in XXXX-XXXX-XXXX format
 */
export function validatePhilSysFormat(philsysNumber: string): boolean {
  const philsysRegex = /^\d{4}-\d{4}-\d{4}$/;
  return philsysRegex.test(philsysNumber);
}

/**
 * Sanitize mobile number for Philippine format
 */
export function sanitizePhone(input: string | null): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  return sanitizeInput(input, {
    allowedChars: /[0-9+\-\s()]/,
    maxLength: 20,
    normalizeUnicode: false,
    removeHtml: true,
  });
}

/**
 * Sanitize mobile number for Philippine format with proper formatting
 */
export function sanitizeMobileNumber(input: string | null): string {
  if (!input) return '';

  // Remove all non-digits
  const digitsOnly = input.replace(/\D/g, '');

  // Handle different Philippine mobile number formats
  if (digitsOnly.startsWith('63')) {
    // +63 format
    return `+${digitsOnly}`;
  } else if (digitsOnly.startsWith('0')) {
    // 0xxx format - convert to +63
    return `+63${digitsOnly.substring(1)}`;
  } else if (digitsOnly.length === 10) {
    // xxx format - add +63
    return `+63${digitsOnly}`;
  }

  return input.trim();
}

/**
 * Validate Philippine mobile number format
 */
export function validatePhilippineMobile(mobile: string): boolean {
  // Philippine mobile number patterns
  const mobileRegex = /^(\+63|0)[89]\d{9}$/;
  return mobileRegex.test(mobile.replace(/\s/g, ''));
}

/**
 * Sanitize email address
 */
export function sanitizeEmail(email: string | null): string {
  if (!email || typeof email !== 'string') {
    return '';
  }

  return sanitizeInput(email.toLowerCase(), {
    allowedChars: /[a-zA-Z0-9@._-]/,
    maxLength: 254, // RFC 5321 limit
    normalizeUnicode: false,
    removeHtml: true,
  });
}

/**
 * Validate email format
 */
export function validateEmailFormat(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email) && email.length <= 254;
}

/**
 * Sanitize PSGC codes (barangay codes)
 * Barangay codes are typically 9-10 digit numbers
 */
export function sanitizeBarangayCode(input: string | null): string {
  if (!input) return '';

  // Barangay codes are typically 9-10 digit numbers
  const digitsOnly = input.replace(/\D/g, '');
  return digitsOnly.substring(0, 10);
}

/**
 * Validate PSGC (Philippine Standard Geographic Code)
 */
export function validatePSGC(code: string): boolean {
  const psgcRegex = /^\d{9,10}$/;
  return psgcRegex.test(code);
}

/**
 * Sanitize search query
 */
export function sanitizeSearchQuery(query: string): string {
  if (!query || typeof query !== 'string') {
    return '';
  }

  return sanitizeInput(query, {
    allowedChars: /[a-zA-Z0-9√Ä-√ø√±√ë\s\-'.]/,
    maxLength: 100,
    normalizeUnicode: true,
    trim: true,
  });
}

/**
 * Generic sanitization function with configurable type-specific processing
 * CONSOLIDATED from multiple implementations
 */
export function sanitizeByType(
  input: string | null,
  type: SanitizationType,
  options: SanitizationOptions = {}
): string {
  // Handle null/empty inputs - early exit for performance
  if (!input) {
    return '';
  }

  // No sanitization - pass through for maximum performance
  if (type === 'none') {
    return input;
  }

  // Early length check to avoid unnecessary processing
  if (options.maxLength && input.length > options.maxLength * 2) {
    input = input.substring(0, options.maxLength * 2);
  }

  let result: string;

  // Apply type-specific sanitization
  switch (type) {
    case 'text':
      result = sanitizeInput(input, options);
      break;

    case 'name':
      result = sanitizeName(input);
      break;

    case 'email':
      result = sanitizeEmail(input);
      break;

    case 'mobile':
      result = sanitizeMobileNumber(input);
      break;

    case 'philsys':
      result = sanitizePhilSysNumber(input);
      break;

    case 'psgc':
      result = sanitizeBarangayCode(input);
      break;

    case 'numeric':
      result = input.replace(/[^\d]/g, '');
      break;

    default:
      result = sanitizeInput(input, options);
  }

  // Apply additional options
  if (options.maxLength && result.length > options.maxLength) {
    result = result.substring(0, options.maxLength);
  }

  if (options.customPattern && !options.customPattern.test(result)) {
    result = options.replacement || '';
  }

  return result;
}

/**
 * Enhanced sanitization function for objects with field-specific rules
 */
export function sanitizeObjectByFieldTypes(
  data: Record<string, any>,
  fieldTypeMap?: Record<string, SanitizationType>
): Record<string, any> {
  // Early exit for empty objects
  if (!data || Object.keys(data).length === 0) {
    return {};
  }

  const sanitized: Record<string, any> = {};

  // Use cached field types for performance
  const fieldTypes = fieldTypeMap
    ? { ...DEFAULT_FIELD_TYPE_MAPPING, ...fieldTypeMap }
    : DEFAULT_FIELD_TYPE_MAPPING;

  // Optimized iteration using for...in for better performance
  for (const key in data) {
    if (data.hasOwnProperty(key)) {
      const value = data[key];
      if (typeof value === 'string' && value.length > 0) {
        const sanitizationType = fieldTypes[key] || 'text';
        sanitized[key] = sanitizeByType(value, sanitizationType);
      } else {
        sanitized[key] = value;
      }
    }
  }

  return sanitized;
}

/**
 * Deep sanitize object (recursively sanitize all string values)
 */
export function sanitizeObject<T extends Record<string, any>>(
  obj: T,
  options: SanitizationOptions = {}
): T {
  if (!obj || typeof obj !== 'object') {
    return obj;
  }

  const sanitized = { ...obj };

  for (const [key, value] of Object.entries(sanitized)) {
    if (typeof value === 'string') {
      (sanitized as any)[key] = sanitizeInput(value, options);
    } else if (Array.isArray(value)) {
      (sanitized as any)[key] = value.map(item =>
        typeof item === 'string' ? sanitizeInput(item, options) : item
      );
    } else if (value && typeof value === 'object') {
      (sanitized as any)[key] = sanitizeObject(value, options);
    }
  }

  return sanitized;
}

/**
 * Rate limiting utility for form submissions
 */
const submissionAttempts = new Map<string, { count: number; lastAttempt: number }>();

export function checkRateLimit(identifier: string, maxAttempts = 5, windowMs = 300000): boolean {
  const now = Date.now();
  const attempts = submissionAttempts.get(identifier);

  if (!attempts || now - attempts.lastAttempt > windowMs) {
    submissionAttempts.set(identifier, { count: 1, lastAttempt: now });
    return true;
  }

  if (attempts.count >= maxAttempts) {
    return false;
  }

  attempts.count++;
  attempts.lastAttempt = now;
  submissionAttempts.set(identifier, attempts);

  return true;
}

/**
 * Common Search Utilities
 *
 * @description Shared utilities and patterns for search functionality across hooks.
 * Extracted from multiple search hooks to eliminate duplication.
 */

import { useState, useEffect, useMemo } from 'react';

import { useDebounce } from '@/hooks/utilities/useDebounce';

// Re-export consolidated search utility types
export type {
  BaseSearchConfig,
  PaginatedSearchConfig,
  PaginatedSearchResult,
  SearchState,
  PaginatedSearchState,
  SearchFunction,
  PaginatedSearchFunction
} from '@/types/utilities';

// Import for local usage
import type {
  SearchState,
  PaginatedSearchState,
  BaseSearchConfig,
  PaginatedSearchConfig,
  PaginatedSearchResult,
  SearchFunction,
  PaginatedSearchFunction
} from '@/types/utilities';

/**
 * Create search state with default values
 */
export function createSearchState<T>(initialQuery = ''): SearchState<T> {
  return {
    query: initialQuery,
    results: [],
    isLoading: false,
    error: null,
  };
}

/**
 * Create paginated search state with default values
 */
export function createPaginatedSearchState<T>(
  initialQuery = '',
  initialPageSize = 20
): PaginatedSearchState<T> {
  return {
    ...createSearchState<T>(initialQuery),
    pagination: {
      current: 1,
      pageSize: initialPageSize,
      total: 0,
      hasMore: false,
    },
  };
}

/**
 * Generic search error handler
 */
export function createSearchErrorHandler(onError?: (error: Error) => void) {
  return (error: unknown, fallbackMessage = 'Search failed') => {
    const searchError = error instanceof Error ? error : new Error(fallbackMessage);
    if (onError) {
      onError(searchError);
    }
    return searchError;
  };
}

/**
 * Create search execution function
 */
export function createSearchExecutor<T>(
  searchFn: SearchFunction<T>,
  setState: React.Dispatch<React.SetStateAction<SearchState<T>>>,
  config: BaseSearchConfig
) {
  const { minQueryLength = 0, onError } = config;

  return async (searchQuery: string) => {
    if (searchQuery.length < minQueryLength) {
      setState(prev => ({
        ...prev,
        results: [],
        error: null,
        isLoading: false,
      }));
      return;
    }

    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
    }));

    try {
      const searchResults = await searchFn(searchQuery);
      setState(prev => ({
        ...prev,
        results: searchResults,
        isLoading: false,
      }));
    } catch (error) {
      const searchError = error instanceof Error ? error : new Error('Search failed');
      if (onError) {
        onError(searchError);
      }
      setState(prev => ({
        ...prev,
        results: [],
        error: searchError,
        isLoading: false,
      }));
    }
  };
}

/**
 * Create paginated search execution function
 */
export function createPaginatedSearchExecutor<T, F>(
  searchFn: PaginatedSearchFunction<T, F>,
  setState: React.Dispatch<React.SetStateAction<PaginatedSearchState<T>>>,
  config: PaginatedSearchConfig
) {
  const { minQueryLength = 0, onError } = config;

  return async (searchQuery: string, page = 1, filters?: F, resetResults = true) => {
    if (searchQuery.length < minQueryLength && page === 1) {
      setState(prev => ({
        ...prev,
        results: [],
        pagination: { ...prev.pagination, total: 0, hasMore: false },
        error: null,
        isLoading: false,
      }));
      return;
    }

    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
    }));

    try {
      const response = await searchFn({
        query: searchQuery,
        page,
        pageSize: config.initialPageSize || 20,
        filters,
      });

      setState(prev => ({
        ...prev,
        results: resetResults || page === 1 ? response.data : [...prev.results, ...response.data],
        pagination: {
          current: page,
          pageSize: response.pageSize,
          total: response.total,
          hasMore: response.hasMore,
        },
        isLoading: false,
      }));
    } catch (error) {
      const searchError = error instanceof Error ? error : new Error('Search failed');
      if (onError) {
        onError(searchError);
      }
      setState(prev => ({
        ...prev,
        results: resetResults || page === 1 ? [] : prev.results,
        pagination:
          resetResults || page === 1
            ? { ...prev.pagination, total: 0, hasMore: false }
            : prev.pagination,
        error: searchError,
        isLoading: false,
      }));
    }
  };
}

/**
 * Create common search utilities
 */
export function createSearchUtilities<T>(
  state: SearchState<T>,
  setState: React.Dispatch<React.SetStateAction<SearchState<T>>>,
  executeSearch: (query: string) => Promise<void>
) {
  const clearSearch = () => {
    setState(prev => ({
      ...prev,
      query: '',
      results: [],
      error: null,
    }));
  };

  const refresh = () => {
    executeSearch(state.query);
  };

  const setQuery = (newQuery: string) => {
    setState(prev => ({ ...prev, query: newQuery }));
  };

  return { clearSearch, refresh, setQuery };
}

/**
 * Create paginated search utilities
 */
export function createPaginatedSearchUtilities<T, F>(
  state: PaginatedSearchState<T>,
  setState: React.Dispatch<React.SetStateAction<PaginatedSearchState<T>>>,
  executeSearch: (
    query: string,
    page?: number,
    filters?: F,
    resetResults?: boolean
  ) => Promise<void>
) {
  const baseUtils = createSearchUtilities(
    state,
    setState as React.Dispatch<React.SetStateAction<SearchState<T>>>,
    executeSearch
  );

  const loadMore = () => {
    if (!state.isLoading && state.pagination.hasMore) {
      executeSearch(state.query, state.pagination.current + 1, undefined, false);
    }
  };

  const setPage = (page: number) => {
    executeSearch(state.query, page, undefined, true);
  };

  const setPageSize = (pageSize: number) => {
    setState(prev => ({
      ...prev,
      pagination: { ...prev.pagination, pageSize, current: 1 },
    }));
    executeSearch(state.query, 1, undefined, true);
  };

  return {
    ...baseUtils,
    loadMore,
    setPage,
    setPageSize,
  };
}

/**
 * Memoized search results filter utility
 */
export function useSearchResultsFilter<T>(
  results: T[],
  filterFn: (item: T, query: string) => boolean,
  query: string
) {
  return useMemo(() => {
    if (!query.trim()) return results;
    return results.filter(item => filterFn(item, query));
  }, [results, filterFn, query]);
}

/**
 * Search highlighting utility
 */
export function highlightSearchText(
  text: string,
  query: string,
  className = 'bg-yellow-200'
): string {
  if (!query.trim()) return text;

  const regex = new RegExp(`(${query.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
  return text.replace(regex, `<span class="${className}">$1</span>`);
}

/**
 * Common search result formatting utilities
 */
export const searchFormatters = {
  /**
   * Format name with proper spacing
   */
  formatName: (firstName?: string, middleName?: string, lastName?: string): string => {
    return [firstName, middleName, lastName].filter(Boolean).join(' ') || 'No name';
  },

  /**
   * Format address from parts
   */
  formatAddress: (parts: (string | undefined)[]): string => {
    const filtered = parts.filter(Boolean);
    return filtered.length > 0 ? filtered.join(', ') : 'No address';
  },

  /**
   * Truncate text with ellipsis
   */
  truncateText: (text: string, maxLength = 50): string => {
    if (text.length <= maxLength) return text;
    return text.slice(0, maxLength) + '...';
  },

  /**
   * Format phone number
   */
  formatPhoneNumber: (phone?: string): string => {
    if (!phone) return 'No phone';
    // Basic Philippine number formatting
    const cleaned = phone.replace(/\D/g, '');
    if (cleaned.startsWith('63')) {
      return `+${cleaned.slice(0, 2)} ${cleaned.slice(2, 5)} ${cleaned.slice(5, 8)} ${cleaned.slice(8)}`;
    }
    if (cleaned.startsWith('09')) {
      return `${cleaned.slice(0, 4)} ${cleaned.slice(4, 7)} ${cleaned.slice(7)}`;
    }
    return phone;
  },
};

/**
 * Search performance optimization hooks
 */
export function useSearchCache<T>(key: string, maxSize = 100) {
  const [cache] = useState(() => new Map<string, { data: T[]; timestamp: number }>());
  const maxAge = 5 * 60 * 1000; // 5 minutes

  const getCachedResult = (query: string): T[] | null => {
    const cacheKey = `${key}:${query}`;
    const cached = cache.get(cacheKey);

    if (cached && Date.now() - cached.timestamp < maxAge) {
      return cached.data;
    }

    return null;
  };

  const setCachedResult = (query: string, data: T[]) => {
    const cacheKey = `${key}:${query}`;

    if (cache.size >= maxSize) {
      const firstKey = cache.keys().next().value;
      if (firstKey !== undefined) {
        cache.delete(firstKey);
      }
    }

    cache.set(cacheKey, { data, timestamp: Date.now() });
  };

  return { getCachedResult, setCachedResult };
}

/**
 * Security Utilities
 * Consolidated security, crypto, and PhilSys utilities aligned with database schema
 */

import crypto from 'crypto';

import bcrypt from 'bcryptjs';

import { validatePhilSysFormat } from './sanitization-utils';

const SALT_ROUNDS = 12;

/**
 * Securely hash a PhilSys card number
 */
export async function hashPhilSysNumber(philsysNumber: string): Promise<string> {
  if (!philsysNumber) {
    throw new Error('PhilSys card number is required for hashing');
  }

  if (!validatePhilSysFormat(philsysNumber)) {
    throw new Error('Invalid PhilSys card number format');
  }

  try {
    return await bcrypt.hash(philsysNumber, SALT_ROUNDS);
  } catch (error) {
    console.error('Error hashing PhilSys number:', error);
    throw new Error('Failed to securely hash PhilSys card number');
  }
}

/**
 * Verify a PhilSys card number against its hash
 */
export async function verifyPhilSysNumber(
  philsysNumber: string,
  hashedNumber: string
): Promise<boolean> {
  if (!philsysNumber || !hashedNumber) {
    return false;
  }

  try {
    return await bcrypt.compare(philsysNumber, hashedNumber);
  } catch (error) {
    console.error('Error verifying PhilSys number:', error);
    return false;
  }
}

/**
 * Extract the last 4 digits of PhilSys number for lookup purposes
 */
export function extractPhilSysLast4(philsysNumber: string): string {
  if (!philsysNumber) {
    throw new Error('PhilSys card number is required');
  }

  const digitsOnly = philsysNumber.replace(/\D/g, '');

  if (digitsOnly.length < 4) {
    throw new Error('PhilSys card number must contain at least 4 digits');
  }

  return digitsOnly.slice(-4);
}

/**
 * Mask PhilSys number for display (****-****-****-1234)
 */
export function maskPhilSysNumber(philsysNumber: string): string {
  if (!philsysNumber) {
    return '';
  }

  const last4 = extractPhilSysLast4(philsysNumber);
  return `****-****-****-${last4}`;
}

/**
 * Validate PhilSys card number format (matches database schema constraints)
 */
// validatePhilSysFormat imported from sanitization-utils above - removed duplicate

/**
 * Generate secure random token
 */
export function generateSecureToken(length: number = 32): string {
  return crypto.randomBytes(length).toString('hex');
}

/**
 * Hash data securely
 */
export function hashData(data: string): string {
  return crypto.createHash('sha256').update(data).digest('hex');
}

/**
 * Secure comparison to prevent timing attacks
 */
export function secureCompare(a: string, b: string): boolean {
  if (a.length !== b.length) {
    return false;
  }

  let result = 0;
  for (let i = 0; i < a.length; i++) {
    result |= a.charCodeAt(i) ^ b.charCodeAt(i);
  }

  return result === 0;
}

/**
 * Log security operation for audit trail
 */
export function logSecurityOperation(
  operation: string,
  userId: string,
  context: Record<string, any> = {}
): void {
  const logEntry = {
    operation,
    userId,
    timestamp: new Date().toISOString(),
    context,
  };

  // In production, this would go to a secure audit log
  console.log('[SECURITY AUDIT]', logEntry);
}

/**
