describe('exceedsMaxSize', () => {
  it('should return false when file size is within limit', () => {
    expect(exceedsMaxSize(1048576, 1)).toBe(false); // 1MB = 1MB limit
    expect(exceedsMaxSize(524288, 1)).toBe(false); // 0.5MB < 1MB limit
    expect(exceedsMaxSize(0, 5)).toBe(false); // 0 bytes < 5MB limit
  });

  it('should return true when file size exceeds limit', () => {
    expect(exceedsMaxSize(2097152, 1)).toBe(true); // 2MB > 1MB limit
    expect(exceedsMaxSize(1048577, 1)).toBe(true); // 1MB + 1 byte > 1MB limit
    expect(exceedsMaxSize(10485760, 5)).toBe(true); // 10MB > 5MB limit
  });

  it('should handle edge cases at exact limits', () => {
    expect(exceedsMaxSize(1048576, 1)).toBe(false); // Exactly 1MB
    expect(exceedsMaxSize(5242880, 5)).toBe(false); // Exactly 5MB
  });

  it('should handle zero limits', () => {
    expect(exceedsMaxSize(0, 0)).toBe(false); // 0 bytes = 0MB limit
    expect(exceedsMaxSize(1, 0)).toBe(true); // Any size > 0MB limit
  });

  it('should handle fractional MB limits', () => {
    expect(exceedsMaxSize(524288, 0.5)).toBe(false); // 0.5MB = 0.5MB limit
    expect(exceedsMaxSize(524289, 0.5)).toBe(true); // 0.5MB + 1 byte > 0.5MB limit
  });

  it('should throw error for negative file size', () => {
    expect(() => exceedsMaxSize(-1, 1)).toThrow('File size must be a non-negative finite number');
  });

  it('should throw error for negative max size', () => {
    expect(() => exceedsMaxSize(1000, -1)).toThrow(
      'Maximum size must be a non-negative finite number'
    );
  });

  it('should throw error for non-finite values', () => {
    expect(() => exceedsMaxSize(Infinity, 1)).toThrow(
      'File size must be a non-negative finite number'
    );
    expect(() => exceedsMaxSize(NaN, 1)).toThrow('File size must be a non-negative finite number');
    expect(() => exceedsMaxSize(1000, Infinity)).toThrow(
      'Maximum size must be a non-negative finite number'
    );
    expect(() => exceedsMaxSize(1000, NaN)).toThrow(
      'Maximum size must be a non-negative finite number'
    );
  });

  it('should handle large file sizes accurately', () => {
    // Test with GB-sized files
    const oneGB = 1024 * 1024 * 1024;
    expect(exceedsMaxSize(oneGB, 1024)).toBe(false); // 1GB < 1024MB
    expect(exceedsMaxSize(oneGB, 1023)).toBe(true); // 1GB > 1023MB
  });
});

/**
 * Common Validation Utilities
 *
 * @description Shared utilities and patterns for validation functionality across hooks.
 * Extracted from multiple validation hooks to eliminate duplication.
 */

import { useState, useCallback } from 'react';

import {
  ValidationResult,
  FieldValidationResult,
  BaseValidationConfig,
  ValidateFormFunction,
  ValidateFieldFunction,
} from '@/lib/validation/types';

// Re-export types for easier access
export type {
  ValidationResult,
  FieldValidationResult,
  BaseValidationConfig,
  ValidateFormFunction,
  ValidateFieldFunction,
};
import { isValidEmail, isValidPhilippineMobile } from '@/lib/validation/utilities';
import type { UtilityValidationState as ValidationState } from '@/types/utilities';

import { toTitleCase } from './stringUtils';

// All types are now imported from the centralized validation types
// This eliminates duplication and ensures consistency

// ValidationState moved to src/types/utilities.ts as UtilityValidationState

/**
 * Create initial validation state
 */
export function createValidationState(): ValidationState {
  return {
    errors: {},
    isValid: true,
    hasValidated: false,
  };
}

/**
 * Validation state management hook
 */
export function useValidationState(config: BaseValidationConfig = {}) {
  const [state, setState] = useState<ValidationState>(createValidationState);

  /**
   * Set validation errors
   */
  const setErrors = useCallback(
    (errors: Record<string, string>) => {
      const isValid = Object.keys(errors).length === 0;

      setState({
        errors,
        isValid,
        hasValidated: true,
      });

      // Trigger callbacks
      if (isValid && config.onValidationSuccess) {
        config.onValidationSuccess();
      } else if (!isValid && config.onValidationError) {
        config.onValidationError(errors);
      }
    },
    [config]
  );

  /**
   * Clear all errors
   */
  const clearErrors = useCallback(() => {
    setState(createValidationState());
  }, []);

  /**
   * Set error for specific field
   */
  const setFieldError = useCallback((field: string, error: string) => {
    setState(prev => ({
      ...prev,
      errors: { ...prev.errors, [field]: error },
      isValid: false,
      hasValidated: true,
    }));
  }, []);

  /**
   * Clear error for specific field
   */
  const clearFieldError = useCallback((field: string) => {
    setState(prev => {
      const newErrors = { ...prev.errors };
      delete newErrors[field];
      const isValid = Object.keys(newErrors).length === 0;

      return {
        errors: newErrors,
        isValid,
        hasValidated: prev.hasValidated,
      };
    });
  }, []);

  /**
   * Get error for specific field
   */
  const getFieldError = useCallback(
    (field: string): string | undefined => {
      return state.errors[field];
    },
    [state.errors]
  );

  /**
   * Check if field has error
   */
  const hasFieldError = useCallback(
    (field: string): boolean => {
      return Boolean(state.errors[field]);
    },
    [state.errors]
  );

  return {
    ...state,
    setErrors,
    clearErrors,
    setFieldError,
    clearFieldError,
    getFieldError,
    hasFieldError,
  };
}

/**
 * Create validation executor for forms
 */
export function createFormValidationExecutor<T>(
  validateFn: ValidateFormFunction<T>,
  setErrors: (errors: Record<string, string>) => void
) {
  return (formData: T): ValidationResult => {
    const result = validateFn(formData);

    // Normalize result format
    const normalizedResult: ValidationResult = {
      isValid: result.isValid || result.success === true,
      errors: result.errors || {},
    };

    // Convert ValidationError[] to Record<string, string> if needed
    const errorsForState = Array.isArray(normalizedResult.errors)
      ? normalizedResult.errors.reduce((acc: Record<string, string>, err: any) => {
          acc[err.field || err.path || 'general'] = err.message || err.error || String(err);
          return acc;
        }, {})
      : normalizedResult.errors;

    setErrors(errorsForState);

    return normalizedResult;
  };
}

/**
 * Create field validation executor
 */
export function createFieldValidationExecutor(
  validateFn: ValidateFieldFunction,
  setFieldError: (field: string, error: string) => void,
  clearFieldError: (field: string) => void
) {
  return (fieldName: string, value: any): FieldValidationResult => {
    const result = validateFn(fieldName, value);

    if (result.isValid) {
      clearFieldError(fieldName);
    } else if (result.error) {
      setFieldError(fieldName, result.error);
    }

    return result;
  };
}

/**
 * Common validation utilities
 */
export const validationUtils = {
  /**
   * Check if value is empty
   */
  isEmpty: (value: any): boolean => {
    return value === null || value === undefined || value === '';
  },

  /**
   * Check if string is valid email (uses centralized string utility)
   */
  isValidEmail,

  /**
   * Check if string is valid Philippine mobile number (uses centralized string utility)
   */
  isValidPhilippineMobile,

  /**
   * Check if string contains only letters and common name characters
   */
  isValidName: (name: string): boolean => {
    return /^[a-zA-Z\s\-'\.]*$/.test(name);
  },

  /**
   * Check if number is within range
   */
  isInRange: (value: number, min: number, max: number): boolean => {
    return value >= min && value <= max;
  },

  /**
   * Validate required fields
   */
  validateRequiredFields: (
    data: Record<string, any>,
    requiredFields: string[]
  ): Record<string, string> => {
    const errors: Record<string, string> = {};

    requiredFields.forEach(field => {
      if (validationUtils.isEmpty(data[field])) {
        errors[field] = `${field.replace(/([A-Z])/g, ' $1').toLowerCase()} is required`;
      }
    });

    return errors;
  },

  /**
   * Merge validation errors from multiple sources
   */
  mergeValidationErrors: (...errorSets: Record<string, string>[]): Record<string, string> => {
    return errorSets.reduce((merged, errors) => ({ ...merged, ...errors }), {});
  },

  /**
   * Format field name for display (uses centralized string utility)
   */
  formatFieldName: (fieldName: string): string => {
    const formatted = fieldName.replace(/([A-Z])/g, ' $1').trim();
    return toTitleCase(formatted);
  },

  /**
   * Create field validator with custom message
   */
  createFieldValidator: (
    predicate: (value: any) => boolean,
    errorMessage: string
  ): ValidateFieldFunction => {
    return (fieldName: string, value: any): FieldValidationResult => {
      const isValid = predicate(value);
      return {
        isValid,
        error: isValid ? undefined : errorMessage,
      };
    };
  },

  /**
   * Create required field validator
   */
  createRequiredValidator: (customMessage?: string): ValidateFieldFunction => {
    return (fieldName: string, value: any): FieldValidationResult => {
      const isValid = !validationUtils.isEmpty(value);
      const fieldDisplayName = validationUtils.formatFieldName(fieldName);

      return {
        isValid,
        error: isValid ? undefined : customMessage || `${fieldDisplayName} is required`,
      };
    };
  },

  /**
   * Create length validator
   */
  createLengthValidator: (
    minLength?: number,
    maxLength?: number,
    customMessage?: string
  ): ValidateFieldFunction => {
    return (fieldName: string, value: any): FieldValidationResult => {
      const stringValue = String(value || '');
      const length = stringValue.length;

      let isValid = true;
      let error: string | undefined;

      if (minLength !== undefined && length < minLength) {
        isValid = false;
        error = customMessage || `Minimum length is ${minLength} characters`;
      } else if (maxLength !== undefined && length > maxLength) {
        isValid = false;
        error = customMessage || `Maximum length is ${maxLength} characters`;
      }

      return { isValid, error };
    };
  },

  /**
   * Create regex validator
   */
  createRegexValidator: (pattern: RegExp, errorMessage: string): ValidateFieldFunction => {
    return (fieldName: string, value: any): FieldValidationResult => {
      const stringValue = String(value || '');
      const isValid = pattern.test(stringValue);

      return {
        isValid,
        error: isValid ? undefined : errorMessage,
      };
    };
  },

  /**
   * Compose multiple validators
   */
  composeValidators: (...validators: ValidateFieldFunction[]): ValidateFieldFunction => {
    return (fieldName: string, value: any): FieldValidationResult => {
      for (const validator of validators) {
        const result = validator(fieldName, value);
        if (!result.isValid) {
          return result;
        }
      }
      return { isValid: true };
    };
  },
};

/**
 * Validation hooks factory
 */
export function createValidationHook<T>(
  validateFormFn: ValidateFormFunction<T>,
  validateFieldFn?: ValidateFieldFunction,
  config: BaseValidationConfig = {}
) {
  return function useValidation() {
    const validationState = useValidationState(config);

    const validateForm = createFormValidationExecutor(validateFormFn, validationState.setErrors);

    const validateField = validateFieldFn
      ? createFieldValidationExecutor(
          validateFieldFn,
          validationState.setFieldError,
          validationState.clearFieldError
        )
      : undefined;

    return {
      ...validationState,
      validateForm,
      validateField,
    };
  };
}

/**
 * Async validation utilities
 */
export const asyncValidationUtils = {
  /**
   * Create debounced async validator
   */
  createDebouncedAsyncValidator: (
    asyncValidator: (value: any) => Promise<FieldValidationResult>,
    delay = 500
  ) => {
    let timeoutId: NodeJS.Timeout;

    return (fieldName: string, value: any, onResult: (result: FieldValidationResult) => void) => {
      clearTimeout(timeoutId);

      timeoutId = setTimeout(async () => {
        try {
          const result = await asyncValidator(value);
          onResult(result);
        } catch (error) {
          onResult({
            isValid: false,
            error: 'Validation failed',
          });
        }
      }, delay);
    };
  },

  /**
   * Create batch async validator
   */
  createBatchAsyncValidator: (
    asyncValidators: Record<string, (value: any) => Promise<FieldValidationResult>>
  ) => {
    return async (data: Record<string, any>): Promise<Record<string, string>> => {
      const validationPromises = Object.entries(asyncValidators).map(async ([field, validator]) => {
        try {
          const result = await validator(data[field]);
          return [field, result.error] as const;
        } catch (error) {
          return [field, 'Validation failed'] as const;
        }
      });

      const results = await Promise.all(validationPromises);
      const errors: Record<string, string> = {};

      results.forEach(([field, error]) => {
        if (error) {
          errors[field] = error;
        }
      });

      return errors;
    };
  },
};

/**
 * Validation Utilities
 * Consolidated validation functions aligned with database schema
 */

import type { ValidationError } from '@/types/validation';

/**
 * Check if email is valid
 */
export function isValidEmail(email: string): boolean {
  if (!email || typeof email !== 'string') {
    return false;
  }

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

/**
 * Check if Philippine mobile number is valid
 */
export function isValidPhilippineMobile(mobile: string): boolean {
  if (!mobile || typeof mobile !== 'string') {
    return false;
  }

  const cleaned = mobile.replace(/\D/g, '');
  return /^09\d{9}$/.test(cleaned) || /^639\d{9}$/.test(cleaned);
}

/**
 * Check if PhilSys format is valid (aligned with database schema)
 */
export function isValidPhilSysFormat(philsys: string): boolean {
  if (!philsys || typeof philsys !== 'string') {
    return false;
  }

  return /^\d{4}-\d{4}-\d{4}-\d{4}$/.test(philsys);
}

/**
 * Check if name is valid (contains only allowed characters)
 */
export function isValidName(name: string): boolean {
  if (!name || typeof name !== 'string') {
    return false;
  }

  // Allow letters, spaces, hyphens, apostrophes, periods (Filipino names)
  const nameRegex = /^[a-zA-Z\s\-\.']+$/;
  return nameRegex.test(name) && name.trim().length > 0;
}

/**
 * Check if age is valid and reasonable
 */
export function isValidAge(age: number): boolean {
  return typeof age === 'number' && age >= 0 && age <= 150;
}

/**
 * Validate required field
 */
export function validateRequired(value: any, fieldName: string): ValidationError | null {
  if (value === null || value === undefined || value === '') {
    return {
      field: fieldName,
      message: `${fieldName} is required`,
      code: 'REQUIRED',
    };
  }
  return null;
}

/**
 * Validate field length
 */
export function validateLength(
  value: string,
  fieldName: string,
  min?: number,
  max?: number
): ValidationError | null {
  if (!value) return null;

  if (min && value.length < min) {
    return {
      field: fieldName,
      message: `${fieldName} must be at least ${min} characters`,
      code: 'MIN_LENGTH',
    };
  }

  if (max && value.length > max) {
    return {
      field: fieldName,
      message: `${fieldName} must not exceed ${max} characters`,
      code: 'MAX_LENGTH',
    };
  }

  return null;
}

/**
 * Validate numeric range
 */
export function validateRange(
  value: number,
  fieldName: string,
  min?: number,
  max?: number
): ValidationError | null {
  if (value === null || value === undefined) return null;

  if (min !== undefined && value < min) {
    return {
      field: fieldName,
      message: `${fieldName} must be at least ${min}`,
      code: 'MIN_VALUE',
    };
  }

  if (max !== undefined && value > max) {
    return {
      field: fieldName,
      message: `${fieldName} must not exceed ${max}`,
      code: 'MAX_VALUE',
    };
  }

  return null;
}

/**
 * Validate date format and range
 */
export function validateDate(dateString: string, fieldName: string): ValidationError | null {
  if (!dateString) return null;

  const date = new Date(dateString);
  if (isNaN(date.getTime())) {
    return {
      field: fieldName,
      message: `${fieldName} must be a valid date`,
      code: 'INVALID_DATE',
    };
  }

  // Check reasonable date range (1900 to current year + 1)
  const minYear = 1900;
  const maxYear = new Date().getFullYear() + 1;
  const year = date.getFullYear();

  if (year < minYear || year > maxYear) {
    return {
      field: fieldName,
      message: `${fieldName} must be between ${minYear} and ${maxYear}`,
      code: 'DATE_RANGE',
    };
  }

  return null;
}

/**
 * Create validation error
 */
export function createValidationError(
  field: string,
  message: string,
  code?: string
): ValidationError {
  return { field, message, code: code || 'VALIDATION_ERROR' };
}

/**
 * Combine multiple validation results
 */
export function combineValidationResults(
  ...results: (ValidationError | null)[]
): ValidationError[] {
  return results.filter((result): result is ValidationError => result !== null);
}
